"use strict";(self.webpackChunkimx_docs=self.webpackChunkimx_docs||[]).push([[4342],{82098:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>d,contentTitle:()=>i,default:()=>p,frontMatter:()=>o,metadata:()=>s,toc:()=>c});var t=r(74848),l=r(28453);const o={title:"MVC",slug:"/iOS/adpattern/mvc",excerpt:"MVC \ud328\ud134\uc774\ub780?",sidebar_position:1},i="MVC",s={id:"iOS/adpattern/MVC/mvc",title:"MVC",description:"MVC\ub780?",source:"@site/docs/iOS/adpattern/MVC/mvc.md",sourceDirName:"iOS/adpattern/MVC",slug:"/iOS/adpattern/mvc",permalink:"/Developer_Jun/docs/iOS/adpattern/mvc",draft:!1,unlisted:!1,editUrl:"https://github.com/immutable/imx-docs/tree/main/docs/iOS/adpattern/MVC/mvc.md",tags:[],version:"current",sidebarPosition:1,frontMatter:{title:"MVC",slug:"/iOS/adpattern/mvc",excerpt:"MVC \ud328\ud134\uc774\ub780?",sidebar_position:1},sidebar:"tutorialSidebar",previous:{title:"KVO Cocoa \ud504\ub808\uc784\uc6cc\ud06c",permalink:"/Developer_Jun/docs/iOS/adpattern/kvo"},next:{title:"TCA",permalink:"/Developer_Jun/docs/iOS/adpattern/tca"}},d={},c=[{value:"MVC\ub780?",id:"mvc\ub780",level:3},{value:"\uc11c\ub85c\uc758 \uad00\uacc4",id:"\uc11c\ub85c\uc758-\uad00\uacc4",level:2},{value:"\uc7a5\uc810",id:"\uc7a5\uc810",level:2},{value:"\ub2e8\uc810",id:"\ub2e8\uc810",level:2},{value:"MVC \uc544\ud0a4\ud14d\ucc98\ub294 \uc544\ud0a4\ud14d\ucc98\ub85c \uc0ac\uc6a9\ud558\uae30 \uc801\uc808\ud560\uae4c?",id:"mvc-\uc544\ud0a4\ud14d\ucc98\ub294-\uc544\ud0a4\ud14d\ucc98\ub85c-\uc0ac\uc6a9\ud558\uae30-\uc801\uc808\ud560\uae4c",level:3}];function a(e){const n={code:"code",h1:"h1",h2:"h2",h3:"h3",header:"header",p:"p",strong:"strong",...(0,l.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.header,{children:(0,t.jsx)(n.h1,{id:"mvc",children:"MVC"})}),"\n",(0,t.jsx)("br",{}),"\n",(0,t.jsx)("img",{src:"https://i.imghippo.com/files/ejO2n1722866109.webp",alt:"",border:"0"}),"\n",(0,t.jsx)("br",{}),"\n",(0,t.jsx)(n.h3,{id:"mvc\ub780",children:"MVC\ub780?"}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"MVC"}),", \uc989 Model-View-Controller\ub294 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc758 \uad6c\uc870\ub97c \uc138 \ubd80\ubd84\uc73c\ub85c \ub098\ub204\ub294 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uc544\ud0a4\ud14d\ucc98 \ud328\ud134\uc785\ub2c8\ub2e4. \uac01 \ubd80\ubd84\uc740 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc758 \ub2e4\ub978 \uce21\uba74\uc744 \ub2f4\ub2f9\ud558\uba70, \uc774\ub97c \ud1b5\ud574 \ucf54\ub4dc\uc758 \uad00\ub9ac\uac00 \uc6a9\uc774\ud574\uc9c0\uace0, \uc720\uc9c0\ubcf4\uc218\uc640 \ud655\uc7a5\uc131\uc774 \ud5a5\uc0c1\ub429\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.code,{children:"Model"}),"\uc740 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\uc11c \uc0ac\uc6a9\ub418\ub294 \ub370\uc774\ud130\uc640 \uadf8 \ub370\uc774\ud130\ub97c \ucc98\ub9ac\ud558\ub294 \ube44\uc988\ub2c8\uc2a4 \ub85c\uc9c1\uc744 \uc815\uc758\ud569\ub2c8\ub2e4. \ubaa8\ub378\uc740 \uc571\uc758 \ub370\uc774\ud130 \uad6c\uc870\ub97c \uc815\uc758\ud558\uace0, \uc774 \ub370\uc774\ud130\uac00 \uc0ac\uc6a9\uc790 \uc778\ud130\ud398\uc774\uc2a4(UI)\uc640\ub294 \ub3c5\ub9bd\uc801\uc73c\ub85c \uc874\uc7ac\ud568\uc744 \ubcf4\uc7a5\ud569\ub2c8\ub2e4. \ubaa8\ub378\uc740 UI\uc758 \uad00\ub9ac \ubcc0\uc218\ub97c \ud3ec\ud568\ud558\uc9c0 \uc54a\uc73c\uba70, \uc774\ub294 \ub300\uc2e0 ViewController\uac00 \ub2f4\ub2f9\ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c \ubaa8\ub378\uc740 \uc21c\uc218\ud558\uac8c \ub370\uc774\ud130\uc640 \uadf8 \ub370\uc774\ud130\uc758 \ucc98\ub9ac\ub97c \ub2f4\ub2f9\ud558\ub294 \ub85c\uc9c1\ub9cc\uc744 \ud3ec\ud568\ud569\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.code,{children:"View"}),"\ub294 \uc0ac\uc6a9\uc790\uc5d0\uac8c \ubcf4\uc5ec\uc9c0\ub294 \uc694\uc18c\uc785\ub2c8\ub2e4. \uc774\ub294 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc758 \uc0ac\uc6a9\uc790 \uc778\ud130\ud398\uc774\uc2a4\ub97c \uad6c\uc131\ud558\uba70, \ub2e8\uc21c\ud55c \ubdf0\uc640 \ub354 \ubcf5\uc7a1\ud55c \ucee4\uc2a4\ud140 \ubdf0\ub97c \uc81c\uacf5\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ubdf0\ub294 \uc704\uce58 \uc815\ubcf4\ub098 \ud45c\uc2dc\ud560 \ub370\uc774\ud130\ub97c \ud30c\ub77c\ubbf8\ud130\ub85c \ubc1b\uc544\uc11c \uc0dd\uc131\ub418\uba70, \uc2a4\ud1a0\ub9ac\ubcf4\ub4dc \ud30c\uc77c \ub4f1\uc758 \ub9ac\uc18c\uc2a4\ub97c \ud3ec\ud568\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ubdf0\ub294 \uc624\uc9c1 \uc0ac\uc6a9\uc790\uc5d0\uac8c \uc815\ubcf4\ub97c \ud45c\uc2dc\ud558\ub294 \uc5ed\ud560\ub9cc\uc744 \ub2f4\ub2f9\ud558\uba70, \uadf8 \uc790\uccb4\ub85c\ub294 \uc5b4\ub5a4 \ub85c\uc9c1\ub3c4 \ucc98\ub9ac\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.code,{children:"Controller"}),"\ub294 \ubaa8\ub378\uacfc \ubdf0 \uc0ac\uc774\uc758 \uc911\uc7ac\uc790 \uc5ed\ud560\uc744 \ud558\uba70, \ub450 \ucef4\ud3ec\ub10c\ud2b8\uc758 \uc5f0\uacb0 \ub2e4\ub9ac \uc5ed\ud560\uc744 \ud569\ub2c8\ub2e4. \ucee8\ud2b8\ub864\ub7ec\ub294 \ubaa8\ub378\ub85c\ubd80\ud130 \ub370\uc774\ud130\ub97c \ubc1b\uc544, \uadf8 \ub370\uc774\ud130\ub97c \ubdf0\uc5d0 \uc801\uc808\ud788 \ud45c\uc2dc\ud558\ub3c4\ub85d \ud569\ub2c8\ub2e4. \ub610\ud55c \uc0ac\uc6a9\uc790\uc758 \uc785\ub825\uc744 \ucc98\ub9ac\ud558\uace0, \uadf8 \uacb0\uacfc\ub97c \ubaa8\ub378\uc5d0 \ubc18\uc601\ud558\uc5ec \uc571\uc758 \uc0c1\ud0dc\ub97c \uc5c5\ub370\uc774\ud2b8 \ud569\ub2c8\ub2e4. \ucee8\ud2b8\ub864\ub7ec\ub294 API \ud638\ucd9c, \ubc84\ud2bc \uc774\ubca4\ud2b8 \ucc98\ub9ac \ubc0f \ub808\uc774\uc544\uc6c3 \uad00\ub9ac\uc640 \uac19\uc740 \ub2e4\uc591\ud55c \uae30\ub2a5\uc744 \ucd1d\uad04\ud569\ub2c8\ub2e4. \ub9cc\uc57d MVC \ud328\ud134\uc5d0\uc11c \ucee8\ud2b8\ub864\ub7ec\uac00 \uc5c6\ub2e4\uba74, \ubaa8\ub378\uacfc \ubdf0\ub294 \uc11c\ub85c\uc758 \uc874\uc7ac\ub098 \uc0c1\ud0dc\ub97c \uc9c1\uc811\uc801\uc73c\ub85c \uc778\uc2dd\ud558\uac70\ub098 \uac31\uc2e0\ud560 \uc218 \uc5c6\uc2b5\ub2c8\ub2e4."]}),"\n",(0,t.jsx)("br",{}),"\n",(0,t.jsx)(n.h2,{id:"\uc11c\ub85c\uc758-\uad00\uacc4",children:"\uc11c\ub85c\uc758 \uad00\uacc4"}),"\n",(0,t.jsx)(n.p,{children:"Model, View, \uadf8\ub9ac\uace0 Controller \uac04\uc758 \uad00\uacc4\ub294 MVC \uc544\ud0a4\ud14d\ucc98\uc758 \ud575\uc2ec\uc774\uba70, \uac01 \uad6c\uc131 \uc694\uc18c\ub294 \uba85\ud655\ud788 \uc815\uc758\ub41c \uc5ed\ud560\uacfc \uc0c1\ud638\uc791\uc6a9 \ubc29\uc2dd\uc744 \uac00\uc9c0\uace0 \uc788\uc2b5\ub2c8\ub2e4."}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"Model\uacfc Controller \uac04\uc758 \uad00\uacc4"}),"\ub294 \uc0c1\ub2f9\ud788 \uae34\ubc00\ud569\ub2c8\ub2e4. Controller\ub294 Model\uc5d0 \uc9c1\uc811 \uc811\uadfc\ud560 \uc218 \uc788\ub294 \uad8c\ud55c\uc744 \uac00\uc9c0\uace0 \uc788\uc5b4, \ub370\uc774\ud130\ub97c \uc77d\uac70\ub098 \uc218\uc815\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \uc0ac\uc6a9\uc790\uc758 \uc785\ub825\uc5d0 \uae30\ubc18\ud55c \ub370\uc774\ud130 \ucc98\ub9ac\uac00 \uac00\ub2a5\ud574\uc9d1\ub2c8\ub2e4. \ubc18\uba74, Model\uc740 Controller\uc5d0 \uc9c1\uc811\uc801\uc73c\ub85c \uc811\uadfc\ud558\ub294 \ub300\uc2e0, \ubcc0\ud654\uac00 \uc788\uc744 \ub54c\ub9c8\ub2e4 Notification\uc774\ub098 Key-Value Observing(KVO) \ubc29\uc2dd\uc744 \ud1b5\ud574 \uc774\ub7ec\ud55c \ubcc0\ud654\ub97c Controller\uc5d0\uac8c \uc54c\ub9bd\ub2c8\ub2e4. \uc774\ub7f0 \ubc29\uc2dd\uc740 Model\uc758 \ub3c5\ub9bd\uc131\uc744 \uc720\uc9c0\ud558\uba74\uc11c\ub3c4 \ud544\uc694\ud55c \uc815\ubcf4\ub97c Controller\uc5d0\uac8c \ud6a8\uacfc\uc801\uc73c\ub85c \uc804\ub2ec\ud560 \uc218 \uc788\uac8c \ud569\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"Model\uacfc View \uac04\uc758 \uad00\uacc4"}),"\ub294 \uc644\uc804\ud788 \ub3c5\ub9bd\uc801\uc785\ub2c8\ub2e4. Model\uc740 UI\uc640 \ubb34\uad00\ud558\uac8c \ub370\uc774\ud130\uc640 \ube44\uc988\ub2c8\uc2a4 \ub85c\uc9c1\uc5d0\ub9cc \uc9d1\uc911\ud558\uba70, View\uc758 \uc874\uc7ac\ub098 \uc0c1\ud0dc\ub97c \uc54c\uc9c0 \ubabb\ud569\ub2c8\ub2e4. \ub9c8\ucc2c\uac00\uc9c0\ub85c View\ub3c4 Model\uc758 \uad6c\uccb4\uc801\uc778 \uad6c\ud604\uc774\ub098 \uc0c1\ud0dc \ubcc0\ud654\uc5d0 \ub300\ud574 \uc9c1\uc811\uc801\uc778 \uc815\ubcf4\ub97c \uac00\uc9c0\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ubd84\ub9ac\ub294 View\uac00 \ub2e8\uc21c\ud788 \ub370\uc774\ud130\ub97c \ud45c\ud604\ud558\ub294 \uc5ed\ud560\uc5d0\ub9cc \uc9d1\uc911\ud560 \uc218 \uc788\uac8c \ud558\uba70, \ub514\uc790\uc778 \ubcc0\uacbd\uc774\ub098 UI \uc218\uc815\uc774 Model\uc774\ub098 Controller\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce58\uc9c0 \uc54a\ub3c4\ub85d \ud569\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"View\uc640 Controller \uac04\uc758 \uad00\uacc4"}),"\ub294 \ub9e4\uc6b0 \uc0c1\ud638\uc791\uc6a9\uc801\uc785\ub2c8\ub2e4. Controller\ub294 View\uc758 \ub2e4\uc591\ud55c \uc694\uc18c\ub4e4\uc5d0 \ub300\ud55c \ucc38\uc870\ub97c \uac00\uc9c0\uace0 \uc788\uc73c\uba70(outlet\uc744 \ud1b5\ud574), \uc774\ub97c \ud1b5\ud574 View\uc758 \uc0c1\ud0dc\ub97c \uc5c5\ub370\uc774\ud2b8\ud558\uac70\ub098 UI \uc694\uc18c\ub97c \uc81c\uc5b4\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. View\ub294 \uc0ac\uc6a9\uc790\uc758 \uc785\ub825\uacfc \ud589\ub3d9\uc744 Controller\uc5d0\uac8c \uc804\ub2ec\ud558\ub294 \uc5ed\ud560\uc744 \ud569\ub2c8\ub2e4. \uc774\ub294 \uc8fc\ub85c delegate \ud328\ud134, data source \uba54\ucee4\ub2c8\uc998, \uadf8\ub9ac\uace0 action-target \uad6c\uc870\ub97c \ud1b5\ud574 \uc774\ub8e8\uc5b4\uc9d1\ub2c8\ub2e4. \uc0ac\uc6a9\uc790\uac00 UI\uc5d0\uc11c \ud2b9\uc815 \ud589\uc704\ub97c \ud560 \ub54c, View\ub294 \uc774\ub97c \uac10\uc9c0\ud558\uace0 \ud574\ub2f9 \uc815\ubcf4\ub97c Controller\uc5d0\uac8c \uc804\ub2ec\ud558\uc5ec \uc801\uc808\ud55c \ubc18\uc751\uc774\ub098 \ub370\uc774\ud130 \uac31\uc2e0\uc774 \uc774\ub8e8\uc5b4\uc9c0\ub3c4\ub85d \ud569\ub2c8\ub2e4."]}),"\n",(0,t.jsx)(n.p,{children:"\uc774\ub7ec\ud55c \uad6c\uc870\uc801\uc778 \ubd84\ub9ac\uc640 \uc0c1\ud638\uc791\uc6a9\uc740 \uac01 \ucef4\ud3ec\ub10c\ud2b8\uac00 \uc11c\ub85c\uc758 \uc5ed\ud560\uc744 \uba85\ud655\ud788 \uc774\ud574\ud558\uace0, \uadf8\uc5d0 \ub9de\uac8c \ucd5c\uc801\ud654\ub41c \ubc29\uc2dd\uc73c\ub85c \ub3d9\uc791\ud560 \uc218 \uc788\uac8c \ud574\uc90d\ub2c8\ub2e4. MVC\ub294 \uc774\ub7f0 \ubc29\uc2dd\uc73c\ub85c \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc758 \uc720\uc9c0\ubcf4\uc218\uc131\uc744 \ub192\uc774\uace0, \ud655\uc7a5\uc131\uc744 \uac1c\uc120\ud558\ub294 \ub370 \ud06c\uac8c \uae30\uc5ec\ud569\ub2c8\ub2e4."}),"\n",(0,t.jsx)("br",{}),"\n",(0,t.jsx)(n.h2,{id:"\uc7a5\uc810",children:"\uc7a5\uc810"}),"\n",(0,t.jsx)(n.p,{children:"\uc18c\uaddc\ubaa8 \ud504\ub85c\uc81d\ud2b8\uc758 \uacbd\uc6b0, MVC \ud328\ud134\uc740 \ube60\ub978 \ubc18\ubcf5\uacfc \uc218\uc815\uc744 \uac00\ub2a5\ud558\uac8c \ud558\uc5ec, \uc544\uc774\ub514\uc5b4\ub97c \uc2e0\uc18d\ud558\uac8c \uad6c\ud604\ud558\uace0 \uc2dc\uc7a5\uc5d0 \ucd9c\uc2dc\ud558\ub294 \ub370 \ud070 \ub3c4\uc6c0\uc744 \uc90d\ub2c8\ub2e4. \uc774\ub294 \uc2a4\ud0c0\ud2b8\uc5c5\uc774\ub098 \uc18c\uaddc\ubaa8 \ud300\uc5d0\uac8c \ud2b9\ud788 \uc720\ub9ac\ud558\uba70, \ucd08\uae30 \uac1c\ubc1c \ub2e8\uacc4\uc5d0\uc11c \ube60\ub978 \ud53c\ub4dc\ubc31\uacfc \uac1c\uc120\uc744 \ud1b5\ud574 \uc81c\ud488\uc744 \uc131\uacf5\uc801\uc73c\ub85c \ub860\uce6d\ud560 \uc218 \uc788\ub294 \uae30\ubc18\uc744 \ub9c8\ub828\ud569\ub2c8\ub2e4."}),"\n",(0,t.jsx)(n.p,{children:"iOS \uac1c\ubc1c\uc5d0 \uc788\uc5b4\uc11c\ub294 Apple\uc758 \uac15\ub825\ud55c \uc9c0\uc6d0 \ub355\ubd84\uc5d0 MVC \ud328\ud134\uc744 \uc0ac\uc6a9\ud558\ub294 \uac83\uc774 \ub354\uc6b1 \uc720\ub9ac\ud569\ub2c8\ub2e4. \uac1c\ubc1c\uc790\ub294 Apple\uc758 \uad11\ubc94\uc704\ud55c \ubb38\uc11c\uc640 \ucee4\ubba4\ub2c8\ud2f0 \ub9ac\uc18c\uc2a4\ub97c \ud65c\uc6a9\ud558\uc5ec \ube60\ub974\uac8c \uae30\uc220\uc744 \uc2b5\ub4dd\ud558\uace0 \ubb38\uc81c\ub97c \ud574\uacb0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uc9c0\uc6d0\uc740 \uac1c\ubc1c\uc790\uac00 \ub354 \uc548\uc815\uc801\uc774\uace0 \uc131\ub2a5\uc774 \uc6b0\uc218\ud55c \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \uad6c\ucd95\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4."}),"\n",(0,t.jsx)(n.p,{children:"\ub610\ud55c, MVC \ud328\ud134\uc740 \uac1c\ubc1c\uc790 \ucee4\ubba4\ub2c8\ud2f0 \ub0b4\uc5d0\uc11c\ub3c4 \ub110\ub9ac \ubc1b\uc544\ub4e4\uc5ec\uc838 \uc788\uc5b4, \uc0c8\ub85c\uc6b4 \uac1c\ubc1c\uc790\uac00 \ud504\ub85c\uc81d\ud2b8 \ud300\uc5d0 \ud569\ub958\ud560 \ub54c\uc758 \ud559\uc2b5 \ubd80\ub2f4\uc744 \uc904\uc5ec\uc90d\ub2c8\ub2e4. \ud504\ub85c\uc81d\ud2b8\uc5d0 \ucc38\uc5ec\ud558\ub294 \ubaa8\ub4e0 \uc774\uac00 \uacf5\ud1b5\ub41c \uc544\ud0a4\ud14d\ucc98\ub97c \uc774\ud574\ud558\uace0 \uc788\uae30 \ub54c\ubb38\uc5d0, \ud300 \ub0b4 \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc774 \uc6d0\ud65c\ud574\uc9c0\uace0, \uacb0\uacfc\uc801\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8\uc758 \uc804\ubc18\uc801\uc778 \uc9c4\ud589 \uc18d\ub3c4\uac00 \ube68\ub77c\uc9d1\ub2c8\ub2e4."}),"\n",(0,t.jsx)(n.p,{children:"\uacb0\uad6d, MVC \ud328\ud134\uc740 \uadf8 \uad6c\uc870\uc758 \uac04\uacb0\ud568\uacfc \ud6a8\uc728\uc131 \ub355\ubd84\uc5d0 \ub2e4\uc591\ud55c \ud504\ub85c\uc81d\ud2b8\uc640 \ud658\uacbd\uc5d0\uc11c \uc9c0\uc18d\uc801\uc73c\ub85c \uc120\ud638\ub418\ub294 \uc124\uacc4 \ubc29\uc2dd\uc785\ub2c8\ub2e4. \ubcf5\uc7a1\ud55c \uc544\ud0a4\ud14d\ucc98\ub97c \uc694\uad6c\ud558\uc9c0 \uc54a\ub294 \ud504\ub85c\uc81d\ud2b8\ub098 \uaddc\ubaa8\uac00 \uc791\uc740 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ud2b9\ud788 \uc801\ud569\ud558\uba70, \uac1c\ubc1c \uc18d\ub3c4\uc640 \uc720\uc9c0\ubcf4\uc218\uc758 \uc6a9\uc774\uc131\uc744 \ud1b5\ud574 \uac80\uc99d\ub41c \uc120\ud0dd\uc73c\ub85c \uc790\ub9ac \ub9e4\uae40\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4."}),"\n",(0,t.jsx)("br",{}),"\n",(0,t.jsx)(n.h2,{id:"\ub2e8\uc810",children:"\ub2e8\uc810"}),"\n",(0,t.jsx)(n.p,{children:"\uac00\uc7a5 \ub450\ub4dc\ub7ec\uc9c0\ub294 \ubb38\uc81c \uc911 \ud558\ub098\ub294 View\uc640 Controller\uac00 \uc9c0\ub098\uce58\uac8c \ubc00\uc811\ud558\uac8c \uc5f0\uacb0\ub418\uc5b4 \uc788\ub2e4\ub294 \uc810\uc785\ub2c8\ub2e4. \uc774\ub85c \uc778\ud574 View\uc758 \ub77c\uc774\ud504\uc0ac\uc774\ud074\uc744 Controller\uac00 \uad00\ub9ac\ud558\uac8c \ub418\uba70, \uc774 \ub458\uc744 \ubd84\ub9ac\ud558\uae30\uac00 \uc0c1\ub2f9\ud788 \uc5b4\ub824\uc6cc\uc9d1\ub2c8\ub2e4. \uacb0\uacfc\uc801\uc73c\ub85c, \uc774 \uad6c\uc870\ub294 \ub450 \ucef4\ud3ec\ub10c\ud2b8 \uac04\uc758 \uc758\uc874\uc131\uc744 \ub192\uc774\uba70 \uc7ac\uc0ac\uc6a9\uc131\uc744 \ud06c\uac8c \uc800\ud558\uc2dc\ud0b5\ub2c8\ub2e4."}),"\n",(0,t.jsx)(n.p,{children:"\ub610\ud55c, \uc774\ub7ec\ud55c \ubc00\uc811\ud55c \uc5f0\uacb0\uc740 \uc720\ub2db \ud14c\uc2a4\ud2b8\uc758 \uc9c4\ud589\uc744 \uc5b4\ub835\uac8c \ub9cc\ub4ed\ub2c8\ub2e4. Controller \ub0b4\uc5d0 \ub2e4\uc591\ud55c \ub85c\uc9c1\uc774 \uc9d1\uc911\ub418\uc5b4 \uc788\uae30 \ub54c\ubb38\uc5d0, \uac04\ub2e8\ud55c \uc720\ub2db \ud14c\uc2a4\ud2b8\uc870\ucc28\ub3c4 \ubcf5\uc7a1\ud574\uc9c8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4, \ub124\ud2b8\uc6cc\ud06c \ud638\ucd9c, \uc0ac\uc6a9\uc790 \uc778\ud130\ud398\uc774\uc2a4\uc758 \uc5c5\ub370\uc774\ud2b8, \ub370\uc774\ud130 \ucc98\ub9ac \ub4f1 \ub2e4\uc591\ud55c \uc791\uc5c5\uc774 Controller\uc5d0\uc11c \uc774\ub8e8\uc5b4\uc9c0\uba74, \uc774\ub97c \ubd84\ub9ac\ud558\uc5ec \ud14c\uc2a4\ud2b8\ud558\uae30\uac00 \ub9e4\uc6b0 \ubc88\uac70\ub85c\uc6cc\uc9d1\ub2c8\ub2e4."}),"\n",(0,t.jsx)(n.p,{children:'Controller\uc5d0 \ucf54\ub4dc\uac00 \uacfc\ub3c4\ud558\uac8c \uc9d1\uc911\ub418\ub294 \ud604\uc0c1\uc740 \uac00\ub3c5\uc131\uacfc \ub0b4\ubd80 \uad6c\uc870\uc758 \ubcf5\uc7a1\uc131\uc744 \uc99d\uac00\uc2dc\ud0a4\ub294 \uc8fc\uc694 \uc694\uc778\uc774\uae30\ub3c4 \ud569\ub2c8\ub2e4. \ub9ce\uc740 \ucf54\ub4dc\uac00 Controller\uc5d0 \ubaa8\uc774\uac8c \ub418\uba74, \uc774\ub294 \uacb0\uad6d delegate, dataSource \uad00\ub9ac, \ub124\ud2b8\uc6cc\ud06c \uc694\uccad \ucc98\ub9ac \ub4f1 \ub2e4\uc591\ud55c \uae30\ub2a5\uc758 \uad6c\ud604\uc744 \uc5b4\ub835\uac8c \ub9cc\ub4e4\uba70, \ucf54\ub4dc\uc758 \uac00\ub3c5\uc131\uc744 \ud06c\uac8c \uc800\ud558\uc2dc\ud0b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ubb38\uc81c\ub294 "Massive View Controller"\ub77c\ub294 \uc6a9\uc5b4\uac00 \uc0dd\uaca8\ub098\ub294 \uc6d0\uc778\uc774 \ub418\uc5c8\uc73c\uba70, \ub9ce\uc740 \uac1c\ubc1c\uc790\ub4e4\uc774 \uc774\ub97c \ube44\ud310\uc801\uc73c\ub85c \uc9c0\uc801\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.'}),"\n",(0,t.jsx)(n.p,{children:"\ud504\ub85c\uc81d\ud2b8\uc758 \uaddc\ubaa8\uac00 \ucee4\uc9c8\uc218\ub85d \uc774\ub7ec\ud55c \ubb38\uc81c\ub4e4\uc740 \ub354\uc6b1 \uc2ec\ud654\ub429\ub2c8\ub2e4. \ubcf5\uc7a1\ud574\uc9c4 \ucf54\ub4dc\ub97c \uc720\uc9c0\ubcf4\uc218\ud558\ub294 \uc77c\uc740 \uc810\uc810 \ub354 \ud798\ub4e4\uc5b4\uc9c0\uba70, \ucd08\uae30\uc5d0 \uac04\uacfc\ub41c \uc124\uacc4 \ubb38\uc81c\ub4e4\uc774 \uc2dc\uac04\uc774 \uc9c0\ub0a0\uc218\ub85d \ub354\uc6b1 \ud070 \ube44\uc6a9\uc73c\ub85c \ub3cc\uc544\uc62c \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ub2e8\uc810\ub4e4\uc740 MVC \ud328\ud134\uc758 \uc120\ud0dd\uc744 \uc7ac\uace0\ud560 \ub54c \uc911\uc694\ud55c \uace0\ub824\uc0ac\ud56d\uc774 \ub418\uc5b4\uc57c \ud569\ub2c8\ub2e4."}),"\n",(0,t.jsx)("br",{}),"\n",(0,t.jsx)(n.h3,{id:"mvc-\uc544\ud0a4\ud14d\ucc98\ub294-\uc544\ud0a4\ud14d\ucc98\ub85c-\uc0ac\uc6a9\ud558\uae30-\uc801\uc808\ud560\uae4c",children:"MVC \uc544\ud0a4\ud14d\ucc98\ub294 \uc544\ud0a4\ud14d\ucc98\ub85c \uc0ac\uc6a9\ud558\uae30 \uc801\uc808\ud560\uae4c?"}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"Distribution"}),"\xa0\uce21\uba74\uc5d0\uc11c \ubcf4\uba74, MVC\ub294 View\uc640 Model\uc744 \ubd84\ub9ac\ud558\uc5ec \uc124\uacc4\ud558\uae34 \ud558\uc9c0\ub9cc, View\uc640 Controller \uc0ac\uc774\uc5d0\ub294 \uac15\ud55c \uc758\uc874\uc131\uc774 \uc874\uc7ac\ud569\ub2c8\ub2e4. \uc774\ub85c \uc778\ud574 View\uc640 Controller\uac00 \ubc00\uc811\ud558\uac8c \uc5f0\uacb0\ub418\uc5b4, \ubd84\ub9ac\ub97c \ud1b5\ud55c \uc720\uc5f0\ud55c \uac1c\ubc1c\uc774 \uc5b4\ub835\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uad6c\uc870\ub294 \ucef4\ud3ec\ub10c\ud2b8 \uac04\uc758 \ub3c5\ub9bd\uc801\uc778 \uc6b4\uc6a9\uc744 \uc81c\ud55c\ud558\uace0, \ubcc0\uacbd \uc0ac\ud56d\uc774 \ud55c \ubd80\ubd84\uc5d0\ub9cc \uad6d\ud55c\ub418\uc9c0 \uc54a\uace0 \ub2e4\ub978 \ubd80\ubd84\uc5d0\ub3c4 \uc601\ud5a5\uc744 \ubbf8\uce60 \uc218 \uc788\uc2b5\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"Testability"}),"\uc758 \uad00\uc810\uc5d0\uc11c\ub294, MVC\uc758 \uad6c\uc870\uac00 \ud14c\uc2a4\ud2b8\uc758 \ubc94\uc704\uc5d0 \uc81c\ud55c\uc744 \uc8fc\uae30\ub3c4 \ud569\ub2c8\ub2e4. \ud2b9\ud788, View\uc640 Controller\uac00 \ubc00\uc811\ud558\uac8c \uc5f0\uacb0\ub418\uc5b4 \uc788\uc5b4\uc11c \uc774 \ub450 \ucef4\ud3ec\ub10c\ud2b8\uc758 \ub3c5\ub9bd\uc801\uc778 \ud14c\uc2a4\ud2b8\uac00 \uc5b4\ub835\uc2b5\ub2c8\ub2e4. \uacb0\uacfc\uc801\uc73c\ub85c, \ub300\ubd80\ubd84 Model\uc758 \ub85c\uc9c1\ub9cc\uc774 \ud14c\uc2a4\ud2b8 \uac00\ub2a5\ud55c \uc0c1\ud669\uc774 \ubc1c\uc0dd\ud560 \uc218 \uc788\uc73c\uba70, \uc774\ub294 \uc804\uccb4\uc801\uc778 \ud14c\uc2a4\ud2b8 \ucee4\ubc84\ub9ac\uc9c0\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce69\ub2c8\ub2e4."]}),"\n",(0,t.jsxs)(n.p,{children:[(0,t.jsx)(n.strong,{children:"Easy of Use"}),"\uc5d0 \uc788\uc5b4\uc11c\ub294 MVC\uac00 \ub9e4\uc6b0 \ud6a8\uc728\uc801\uc785\ub2c8\ub2e4. \uc774 \uc544\ud0a4\ud14d\ucc98\ub294 \ube44\uad50\uc801 \uc801\uc740 \uc591\uc758 \ucf54\ub4dc\ub85c \uad6c\ud604\uc774 \uac00\ub2a5\ud558\uba70, \uacbd\ud5d8\uc774 \uc801\uc740 \uac1c\ubc1c\uc790\ub3c4 \uc27d\uac8c \uc811\uadfc\ud558\uace0 \uc720\uc9c0\ubcf4\uc218\ud560 \uc218 \uc788\ub294 \uad6c\uc870\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4. \uc774\ub294 \ud559\uc2b5 \uace1\uc120\uc744 \ub0ae\ucd94\uace0, \uc791\uc740 \uaddc\ubaa8\uc758 \ud504\ub85c\uc81d\ud2b8\ub098 \uac04\ub2e8\ud55c \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uac1c\ubc1c\uc5d0 \uc774\uc810\uc744 \uc81c\uacf5\ud569\ub2c8\ub2e4."]}),"\n",(0,t.jsx)(n.p,{children:"\uadf8\ub7ec\ub098 \ud504\ub85c\uc81d\ud2b8\uc758 \uaddc\ubaa8\uac00 \ucee4\uc9c0\uace0 \uae30\ub2a5\uc774 \ubcf5\uc7a1\ud574\uc9c8\uc218\ub85d, \uadf8\ub9ac\uace0 \ub124\ud2b8\uc6cc\ud06c\ub97c \ud1b5\ud574 \ub2e4\ub8e8\uc5b4\uc57c \ud558\ub294 \ub370\uc774\ud130\uac00 \ub9ce\uc544\uc9c8\uc218\ub85d MVC \uad6c\uc870\ub294 \uc810\uc810 \uadf8 \ud55c\uacc4\ub97c \ub4dc\ub7ec\ub0c5\ub2c8\ub2e4. \ucee8\ud2b8\ub864\ub7ec\uac00 \ubc29\ub300\ud574\uc9c0\uba74\uc11c \uc720\uc9c0\ubcf4\uc218\uac00 \uc5b4\ub824\uc6cc\uc9c0\uace0, \uc774\ub85c \uc778\ud574 \uc720\uc9c0\ubcf4\uc218 \ube44\uc6a9\ub3c4 \uc99d\uac00\ud558\uac8c \ub429\ub2c8\ub2e4. \uc774\ub294 \uacb0\uad6d \ud504\ub85c\uc81d\ud2b8\uc758 \ud655\uc7a5\uc131\uacfc \uc720\uc9c0\ubcf4\uc218\uc131\uc744 \uc800\ud574\ud560 \uc218 \uc788\uc73c\uba70, \ubcf4\ub2e4 \ubcf5\uc7a1\ud55c \uc544\ud0a4\ud14d\ucc98 \ud328\ud134\uc73c\ub85c\uc758 \uc804\ud658\uc774 \uace0\ub824\ub420 \uc218 \uc788\uc2b5\ub2c8\ub2e4."})]})}function p(e={}){const{wrapper:n}={...(0,l.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(a,{...e})}):a(e)}},28453:(e,n,r)=>{r.d(n,{R:()=>i,x:()=>s});var t=r(96540);const l={},o=t.createContext(l);function i(e){const n=t.useContext(o);return t.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function s(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(l):e.components||l:i(e.components),t.createElement(o.Provider,{value:n},e.children)}}}]);